import React, { useState, useEffect } from 'react';
import './HomePage.css';  // For styling

function HomePage() {
  const [stickerPosition, setStickerPosition] = useState(0); // Initial position of the sticker
  const [direction, setDirection] = useState(1); // 1 for moving right, -1 for moving left

  useEffect(() => {
    const interval = setInterval(() => {
      // Get the window width
      const screenWidth = window.innerWidth;
      
      // Update sticker position based on current direction
      setStickerPosition((prevPosition) => {
        let newPosition = prevPosition + direction;

        // If the sticker reaches the edge, change direction
        if (newPosition >= screenWidth - 100 || newPosition <= 0) {
          setDirection(-direction); // Reverse direction
          newPosition = prevPosition + direction; // Adjust new position with new direction
        }

        return newPosition;
      });
    }, 5); // Adjust the interval for smoother movement

    return () => clearInterval(interval); // Cleanup the interval on component unmount
  }, [direction]);

  return (
    <div className="homepage">
      <div className="content">
        <h1>Welcome to this page...</h1>
        <div className="sticker" style={{ left: `${stickerPosition}px` }}>
          <img src="/hi-gif.gif" alt="Hi Sticker" />
        </div>
      </div>
    </div>
  );
}

export default HomePage;
