import React, { useState, useEffect } from 'react';
import './HomePage.css';  // For styling

function HomePage() {
  const [yPosition, setYPosition] = useState(50); // Initial Y position of the GIF (centered vertically)

  useEffect(() => {
    const interval = setInterval(() => {
      // Create a floating effect by moving up and down
      setYPosition((prevYPosition) => {
        // We oscillate the position within a range (e.g., between 40% and 60%)
        if (prevYPosition >= 60) {
          return 50; // Move down
        } else if (prevYPosition <= 40) {
          return 50; // Move up
        }
        return prevYPosition + (prevYPosition > 50 ? -1 : 1); // Move towards the center and back
      });
    }, 15); // Adjust the speed of the animation

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  return (
    <div className="homepage">
      <div className="content">
        <h1>Welcome to My Portfolio</h1>
        <p>
          Hello! I'm <strong>John Doe</strong>, a passionate web developer with experience in building
          beautiful and functional websites and applications.
        </p>
        <p>
          I specialize in <strong>React.js</strong>, <strong>JavaScript</strong>, and <strong>CSS</strong>, and I'm always looking for new challenges and opportunities to learn.
        </p>
        <p>
          On this portfolio, you'll find a collection of my projects, skills, and certifications. Feel free to browse through and check out some of my work!
        </p>
        <p>
          If you have any questions or would like to connect, don't hesitate to visit my <strong>Contact</strong> page.
        </p>

        
      </div>
    </div>
  );
}

export default HomePage;
