import React, { useState, useEffect ,Switch } from 'react';
import './HomePage.css';  // For styling

function HomePage() {
  const [stickerPosition, setStickerPosition] = useState(0); // Starting position of the sticker

  useEffect(() => {
    const interval = setInterval(() => {
      setStickerPosition((prevPosition) => (prevPosition + 1) % window.innerWidth); // Move sticker across the screen
    }, 10); // Change position every 10 milliseconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  return (
    <div className="homepage">
      <div className="content">
        <h1>Welcome to this page...</h1>
        <div className="sticker" style={{ left: `${stickerPosition}px` }}>
          Hi!
        </div>
      </div>
    </div>
  );
}

export default HomePage;
